var limit=200,body=document.body;loop={start:function(){for(var i=0;i<=limit;i++){var a=this.newStar();a.style.top=this.rand()*79+10+"%";a.style.left=this.rand()*99+"%";a.style.webkitAnimationDelay=this.rand()+"s";a.style.mozAnimationDelay=this.rand()+"s";body.appendChild(a)}},rand:function(){return Math.random()},newStar:function(){var d=document.createElement('div');d.innerHTML='<figure class="star"><figure class="star-top"></figure><figure class="star-bottom"></figure></figure>';return d.firstChild},};loop.start();